(272, 'Super Mega Death Rocket!', 1, 0, 'Jinx fires a super rocket across the map that gains damage as it travels. The rocket will explode upon colliding with an enemy champion, dealing damage to it and surrounding enemies based on their missing Health.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/JinxR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:75;i:1;i:65;i:2;i:55;}', 258, 'JinxR', 0, 0, 0),
(273, 'Second Skin', 0, 0, 'Kai\'Sa\'s basic attacks stack Plasma, dealing increasing bonus magic damage. Allies\' immobilizing effects help stack Plasma. Additionally, Kai\'Sa\'s item purchases upgrade her basic spells to have more powerful properties.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Kaisa_Passive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 259, 'passive', 0, 0, 0),
(274, 'Icathian Rain', 1, 0, 'Kai\'Sa shoots a swarm of missiles that seek out nearby targets.<br><br>Living Weapon: Icathian Rain is upgraded to shoot more missiles.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/KaisaQ.png', 'a:5:{i:0;i:55;i:1;i:55;i:2;i:55;i:3;i:55;i:4;i:55;}', 'Mana', 'a:5:{i:0;i:10;i:1;i:9;i:2;i:8;i:3;i:7;i:4;i:6;}', 259, 'KaisaQ', 0, 0, 0),
(275, 'Void Seeker', 1, 0, 'Kai\'Sa shoots a long range missile, marking enemies with her passive.<br><br>Living Weapon: Void Seeker is upgraded to apply more passive marks and reduce cooldown on champion hit.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/KaisaW.png', 'a:5:{i:0;i:55;i:1;i:60;i:2;i:65;i:3;i:70;i:4;i:75;}', 'Mana', 'a:5:{i:0;i:22;i:1;i:20;i:2;i:18;i:3;i:16;i:4;i:14;}', 259, 'KaisaW', 0, 0, 0);
