(333, 'Icathian Surprise', 0, 0, '4 seconds after dying, Kogmaw explodes, dealing true damage to surrounding enemies.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/KogMaw_IcathianSurprise.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 271, 'passive', 0, 0, 1),
(334, 'Caustic Spittle', 1, 0, 'Kog\'Maw launches a corrosive projectile which deals magic damage and corrodes the target\'s armor and magic resist for a short time. Kog\'Maw also gains additional attack speed.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/KogMawQ.png', 'a:5:{i:0;i:40;i:1;i:40;i:2;i:40;i:3;i:40;i:4;i:40;}', 'Mana', 'a:5:{i:0;i:8;i:1;i:8;i:2;i:8;i:3;i:8;i:4;i:8;}', 271, 'KogMawQ', 0, 0, 0),
