(399, 'Double Up', 1, 0, 'Miss Fortune fires a bullet at an enemy, damaging them and a target behind them. Both strikes can also apply Love Tap.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MissFortuneRicochetShot.png', 'a:5:{i:0;i:43;i:1;i:46;i:2;i:49;i:3;i:52;i:4;i:55;}', 'Mana', 'a:5:{i:0;i:7;i:1;i:6;i:2;i:5;i:3;i:4;i:4;i:3;}', 284, 'MissFortuneRicochetShot', 0, 0, 0);
INSERT INTO `spell` (`id`, `name`, `is_active`, `is_passive`, `resume`, `spell_image`, `cost`, `type`, `cooldown`, `champion_id`, `spell_id`, `is_toggle`, `is_both`, `is_champ_passive`) VALUES
(400, 'Strut', 1, 0, 'Miss Fortune passively gains Move Speed when not attacked. This ability can be activated to grant bonus Attack Speed for a short duration. While it\'s on cooldown, Love Taps reduce the remaining cooldown of Strut.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MissFortuneViciousStrikes.png', 'a:5:{i:0;i:45;i:1;i:45;i:2;i:45;i:3;i:45;i:4;i:45;}', 'Mana', 'a:5:{i:0;i:12;i:1;i:12;i:2;i:12;i:3;i:12;i:4;i:12;}', 284, 'MissFortuneViciousStrikes', 0, 0, 0),
(401, 'Make It Rain', 1, 0, 'Miss Fortune reveals an area with a flurry of bullets, dealing waves of damage to opponents and slowing them.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MissFortuneScattershot.png', 'a:5:{i:0;i:80;i:1;i:80;i:2;i:80;i:3;i:80;i:4;i:80;}', 'Mana', 'a:5:{i:0;i:18;i:1;i:16;i:2;i:14;i:3;i:12;i:4;i:10;}', 284, 'MissFortuneScattershot', 0, 0, 0),
(402, 'Bullet Time', 1, 0, 'Miss Fortune channels a barrage of bullets into a cone in front of her, dealing large amounts of damage to enemies. Each wave of Bullet Time can critically strike', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MissFortuneBulletTime.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:120;i:1;i:110;i:2;i:100;}', 284, 'MissFortuneBulletTime', 0, 0, 0),
(403, 'Stone Skin', 0, 0, 'Wukong gains stacking armor and max health regeneration while fighting champions and monsters.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/MonkeyKingStoneSkin.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 285, 'passive', 0, 0, 1),
(404, 'Crushing Blow', 1, 0, 'Wukong\'s next attack gains attack range, deals bonus damage, and reduces the target\'s armor for a few seconds.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MonkeyKingDoubleAttack.png', 'a:5:{i:0;i:40;i:1;i:40;i:2;i:40;i:3;i:40;i:4;i:40;}', 'Mana', 'a:5:{i:0;i:8;i:1;d:7.5;i:2;i:7;i:3;d:6.5;i:4;i:6;}', 285, 'MonkeyKingDoubleAttack', 0, 0, 0),
(405, 'Warrior Trickster', 1, 0, 'Wukong becomes <font color=\'#91d7ee\'>Invisible</font> and dashes in a direction, leaving behind a clone that will attack nearby enemies.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MonkeyKingDecoy.png', 'a:5:{i:0;i:80;i:1;i:70;i:2;i:60;i:3;i:50;i:4;i:40;}', 'mana', 'a:5:{i:0;i:22;i:1;d:19.5;i:2;i:17;i:3;d:14.5;i:4;i:12;}', 285, 'MonkeyKingDecoy', 0, 0, 0),
(406, 'Nimbus Strike', 1, 0, 'Wukong dashes to a targeted enemy and sends out images to attack enemies near his target, dealing damage to each enemy struck.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MonkeyKingNimbus.png', 'a:5:{i:0;i:30;i:1;i:35;i:2;i:40;i:3;i:45;i:4;i:50;}', 'mana', 'a:5:{i:0;i:10;i:1;d:9.5;i:2;i:9;i:3;d:8.5;i:4;i:8;}', 285, 'MonkeyKingNimbus', 0, 0, 0),
(407, 'Cyclone', 1, 0, 'Wukong\'s extends his staff and spins it around repeatedly, gaining Move Speed.<br><br>Enemies struck take damage and are knocked up.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MonkeyKingSpinToWin.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'mana', 'a:3:{i:0;i:120;i:1;i:105;i:2;i:90;}', 285, 'MonkeyKingSpinToWin', 0, 0, 0),
(408, 'Darkness Rise', 0, 0, 'Mordekaiser gains a powerful damage aura and Move Speed after landing 3 attacks or spells against champions or monsters.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/MordekaiserPassive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 286, 'passive', 0, 0, 1),
(409, 'Obliterate', 1, 0, 'Mordekaiser smashes the ground with his mace dealing damage to each enemy struck. Damage is increased when hitting a single enemy.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MordekaiserQ.png', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 'empty', 'a:5:{i:0;i:9;i:1;d:7.75;i:2;d:6.5;i:3;d:5.25;i:4;i:4;}', 286, 'MordekaiserQ', 0, 0, 0),
(410, 'Indestructible', 1, 0, 'Mordekaiser stores damage he deals and takes to create a shield. He may consume the shield to heal.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MordekaiserW.png', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 'empty', 'a:5:{i:0;i:12;i:1;i:11;i:2;i:10;i:3;i:9;i:4;i:8;}', 286, 'MordekaiserW', 0, 0, 0),
(411, 'Death\'s Grasp', 1, 0, 'Mordekaiser pulls all enemies in an area.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MordekaiserE.png', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 'empty', 'a:5:{i:0;i:18;i:1;i:16;i:2;i:14;i:3;i:12;i:4;i:10;}', 286, 'MordekaiserE', 0, 0, 0),
(412, 'Realm of Death', 1, 0, 'Mordekaiser drags his victim to a different dimension with him and steals a portion of their stats. If he kills them, he keeps the stats until the victim respawns.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MordekaiserR.png', 'a:3:{i:0;i:0;i:1;i:0;i:2;i:0;}', 'empty', 'a:3:{i:0;i:140;i:1;i:120;i:2;i:100;}', 286, 'MordekaiserR', 0, 0, 0),
(413, 'Soul Siphon', 0, 0, 'Morgana drains spirit from her enemies, healing as she deals damage to champions, large minions, and medium and larger jungler monsters.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/FallenAngel_Empathize.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 287, 'passive', 0, 0, 1),
(414, 'Dark Binding', 1, 0, 'Morgana binds an enemy in place with dark magic, forcing them to feel the pain they\'ve caused and dealing magic damage. ', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MorganaQ.png', 'a:5:{i:0;i:50;i:1;i:55;i:2;i:60;i:3;i:65;i:4;i:70;}', 'Mana', 'a:5:{i:0;i:10;i:1;i:10;i:2;i:10;i:3;i:10;i:4;i:10;}', 287, 'MorganaQ', 0, 0, 0),
(415, 'Tormented Shadow', 1, 0, 'Morgana casts a cursed shadow on an area, damaging enemies who dare stand in her dark circle. They receive magic damage over time, which increases the lower health they are.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MorganaW.png', 'a:5:{i:0;i:70;i:1;i:85;i:2;i:100;i:3;i:115;i:4;i:130;}', 'Mana', 'a:5:{i:0;i:12;i:1;i:12;i:2;i:12;i:3;i:12;i:4;i:12;}', 287, 'MorganaW', 0, 0, 0),
(416, 'Black Shield', 1, 0, 'Morgana anoints an ally with a protective barrier of starfire, which absorbs magical damage and disabling effects until it is broken.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MorganaE.png', 'a:5:{i:0;i:80;i:1;i:80;i:2;i:80;i:3;i:80;i:4;i:80;}', 'mana', 'a:5:{i:0;i:24;i:1;i:22;i:2;i:20;i:3;i:18;i:4;i:16;}', 287, 'MorganaE', 0, 0, 0),
(417, 'Soul Shackles', 1, 0, 'Morgana unleashes the full force of her Celestial power as she unbinds her wings and hovers above the ground. She lashes chains of dark pain onto nearby enemy champions, gaining Move Speed towards them. The chains slow and deal initial damage and, after a delay, stun those who are unable to break them.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MorganaR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:120;i:1;i:110;i:2;i:100;}', 287, 'MorganaR', 0, 0, 0),
(418, 'Surging Tides', 0, 0, 'When Nami\'s Abilities hit allied champions they gain Move Speed for a short duration.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/NamiPassive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 288, 'passive', 0, 0, 1),
(419, 'Aqua Prison', 1, 0, 'Sends a bubble to a target area, dealing damage and stunning all enemies on impact.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NamiQ.png', 'a:5:{i:0;i:60;i:1;i:60;i:2;i:60;i:3;i:60;i:4;i:60;}', 'mana', 'a:5:{i:0;i:12;i:1;i:11;i:2;i:10;i:3;i:9;i:4;i:8;}', 288, 'NamiQ', 0, 0, 0),
(420, 'Ebb and Flow', 1, 0, 'Unleashes a stream of water that bounces back and forth between allied and enemy champions, healing allies and damaging enemies.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NamiW.png', 'a:5:{i:0;i:70;i:1;i:80;i:2;i:90;i:3;i:100;i:4;i:110;}', 'mana', 'a:5:{i:0;i:10;i:1;i:10;i:2;i:10;i:3;i:10;i:4;i:10;}', 288, 'NamiW', 0, 0, 0),
(421, 'Tidecaller\'s Blessing', 1, 0, 'Empowers an allied champion for a short duration. The ally\'s basic attacks and spells deal bonus magic damage and slow the target.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NamiE.png', 'a:5:{i:0;i:55;i:1;i:60;i:2;i:65;i:3;i:70;i:4;i:75;}', 'mana', 'a:5:{i:0;i:11;i:1;i:11;i:2;i:11;i:3;i:11;i:4;i:11;}', 288, 'NamiE', 0, 0, 0),
(422, 'Tidal Wave', 1, 0, 'Summons a massive Tidal Wave that knocks up, slows, and damages enemies. Allies hit gain double the effect of Surging Tides.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NamiR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'mana', 'a:3:{i:0;i:120;i:1;i:110;i:2;i:100;}', 288, 'NamiR', 0, 0, 0),
(423, 'Soul Eater', 0, 0, 'Nasus drains his foe\'s spiritual energy, giving him bonus Life Steal.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Nasus_Passive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 289, 'passive', 0, 0, 1),
(424, 'Siphoning Strike', 1, 0, 'Nasus strikes his foe, dealing damage and increasing the power of his future Siphoning Strikes if he slays his target.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NasusQ.png', 'a:5:{i:0;i:20;i:1;i:20;i:2;i:20;i:3;i:20;i:4;i:20;}', 'Mana', 'a:5:{i:0;d:7.5;i:1;d:6.5;i:2;d:5.5;i:3;d:4.5;i:4;d:3.5;}', 289, 'NasusQ', 0, 0, 0),
(425, 'Wither', 1, 0, 'Nasus ages an enemy champion, decelerating their Move Speed and Attack Speed over time.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NasusW.png', 'a:5:{i:0;i:80;i:1;i:80;i:2;i:80;i:3;i:80;i:4;i:80;}', 'Mana', 'a:5:{i:0;i:15;i:1;i:14;i:2;i:13;i:3;i:12;i:4;i:11;}', 289, 'NasusW', 0, 0, 0),
(426, 'Spirit Fire', 1, 0, 'Nasus unleashes a spirit flame at a location, dealing damage and reducing the Armor of enemies who stand on it.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NasusE.png', 'a:5:{i:0;i:70;i:1;i:85;i:2;i:100;i:3;i:115;i:4;i:130;}', 'Mana', 'a:5:{i:0;i:12;i:1;i:12;i:2;i:12;i:3;i:12;i:4;i:12;}', 289, 'NasusE', 0, 0, 0),
(427, 'Fury of the Sands', 1, 0, 'Nasus unleashes a mighty sandstorm that batters nearby enemies. While the storm rages, he gains increased Health, Attack Range, damages nearby enemies, has a reduced cooldown on Siphoning Strike, and gains bonus Armor and Magic Resistance.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NasusR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:120;i:1;i:120;i:2;i:120;}', 289, 'NasusR', 0, 0, 0),
(428, 'Staggering Blow', 0, 0, 'Nautilus\' first Attack against a target deals increased physical damage and roots them briefly.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Nautilus_StaggeringBlow.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 290, 'passive', 0, 0, 1),
(429, 'Dredge Line', 1, 0, 'Nautilus hurls his anchor forward.  Colliding with an enemy pulls them and Nautilus together, dealing magic damage.  Colliding with terrain pulls Nautilus towards it.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NautilusAnchorDrag.png', 'a:5:{i:0;i:60;i:1;i:60;i:2;i:60;i:3;i:60;i:4;i:60;}', 'Mana', 'a:5:{i:0;i:14;i:1;i:13;i:2;i:12;i:3;i:11;i:4;i:10;}', 290, 'NautilusAnchorDrag', 0, 0, 0),
(430, 'Titan\'s Wrath', 1, 0, 'Nautilus gains a temporary Shield.  While it persists, his Attacks deal damage over time to his target and surrounding enemies.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NautilusPiercingGaze.png', 'a:5:{i:0;i:80;i:1;i:80;i:2;i:80;i:3;i:80;i:4;i:80;}', 'Mana', 'a:5:{i:0;i:12;i:1;i:12;i:2;i:12;i:3;i:12;i:4;i:12;}', 290, 'NautilusPiercingGaze', 0, 0, 0),
(431, 'Riptide', 1, 0, 'Nautilus creates three exploding waves around himself. Each explosion damages and slows enemies.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NautilusSplashZone.png', 'a:5:{i:0;i:50;i:1;i:60;i:2;i:70;i:3;i:80;i:4;i:90;}', 'Mana', 'a:5:{i:0;i:7;i:1;d:6.5;i:2;i:6;i:3;d:5.5;i:4;i:5;}', 290, 'NautilusSplashZone', 0, 0, 0),
(432, 'Depth Charge', 1, 0, 'Nautilus fires a shockwave into the earth that chases an opponent. This shockwave rips up the earth above it, knocking enemies into the air. When it reaches the opponent, the shockwave erupts, knocking his target into the air and stunning them.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NautilusGrandLine.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:120;i:1;i:100;i:2;i:80;}', 290, 'NautilusGrandLine', 0, 0, 0),
(433, 'Inherent Glamour', 0, 0, 'Neeko can look like an ally champion. Taking damage from enemy Champions or casting damaging spells breaks the disguise.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Neeko_P.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 291, 'passive', 0, 0, 1),
(434, 'Blooming Burst', 1, 0, 'Neeko throws a seed dealing magic damage. The seed blooms again on hitting champions or killing units.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NeekoQ.png', 'a:5:{i:0;i:50;i:1;i:60;i:2;i:70;i:3;i:80;i:4;i:90;}', 'Mana', 'a:5:{i:0;i:7;i:1;i:7;i:2;i:7;i:3;i:7;i:4;i:7;}', 291, 'NeekoQ', 0, 0, 0),
(435, 'Shapesplitter', 1, 0, 'Neeko passively deals bonus magic damage every third attack. Neeko can activate to send a clone in a direction.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NeekoW.png', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 'empty', 'a:5:{i:0;i:16;i:1;i:15;i:2;i:14;i:3;i:13;i:4;i:12;}', 291, 'NeekoW', 0, 0, 0),
(436, 'Tangle-Barbs', 1, 0, 'Neeko slings a tangle that damage and root everything it passes through. If the tangle kills an enemy or passes through a champion, it becomes larger, faster, and roots for longer.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NeekoE.png', 'a:5:{i:0;i:60;i:1;i:65;i:2;i:70;i:3;i:75;i:4;i:80;}', 'Mana', 'a:5:{i:0;i:12;i:1;d:11.5;i:2;i:11;i:3;d:10.5;i:4;i:10;}', 291, 'NeekoE', 0, 0, 0),
(437, 'Pop Blossom', 1, 0, 'After a short preparation, Neeko leaps into the air and gains a shield. Upon landing, nearby enemies are damaged and stunned. The preparation is hidden if Neeko is disguised.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NeekoR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:90;i:1;i:90;i:2;i:90;}', 291, 'NeekoR', 0, 0, 0),
(443, 'Umbra Blades', 0, 0, 'Every few seconds, Nocturne\'s next attack strikes surrounding enemies for bonus physical damage and heals himself. <br><br>Nocturne\'s basic attacks reduce this cooldown.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Nocturne_UmbraBlades.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 293, 'passive', 0, 0, 1),
(444, 'Duskbringer', 1, 0, 'Nocturne throws a shadow blade that deals damage, leaves a Dusk Trail, and causes champions to leave a Dusk Trail. While on the trail, Nocturne can move through units and has increased Move Speed and Attack Damage.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NocturneDuskbringer.png', 'a:5:{i:0;i:60;i:1;i:65;i:2;i:70;i:3;i:75;i:4;i:80;}', 'Mana', 'a:5:{i:0;i:8;i:1;i:8;i:2;i:8;i:3;i:8;i:4;i:8;}', 293, 'NocturneDuskbringer', 0, 0, 0),
(445, 'Shroud of Darkness', 1, 0, 'Nocturne empowers his blades, passively gaining Attack Speed. Activating Shroud of Darkness allows Nocturne to fade into the shadows, creating a magical barrier which blocks a single enemy ability and doubles his passive Attack Speed if successful.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/NocturneShroudofDarkness.png', 'a:5:{i:0;i:50;i:1;i:50;i:2;i:50;i:3;i:50;i:4;i:50;}', 'Mana', 'a:5:{i:0;i:20;i:1;i:18;i:2;i:16;i:3;i:14;i:4;i:12;}', 293, 'NocturneShroudofDarkness', 0, 0, 0),
