(588, 'Glory in Death', 0, 0, 'After being killed, Sion will temporarily reanimate with rapidly decaying Health. His attacks become very rapid, heal him, and deal bonus damage based on his target\'s maximum Health.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Sion_Passive1.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 322, 'passive', 0, 0, 0),
(589, 'Decimating Smash', 1, 0, 'Sion charges a powerful swing in an area in front of himself that will deal damage to enemies when released. If he charges for enough time, enemies hit by the swing will also be knocked up and stunned.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/SionQ.png', 'a:5:{i:0;i:45;i:1;i:45;i:2;i:45;i:3;i:45;i:4;i:45;}', 'Mana', 'a:5:{i:0;i:10;i:1;i:9;i:2;i:8;i:3;i:7;i:4;i:6;}', 322, 'SionQ', 0, 0, 0),
