(620, 'Kingslayer', 1, 0, 'Sylas lunges at an enemy with magical force dealing damage and healing himself against enemy champions.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/SylasW.png', 'a:5:{i:0;i:50;i:1;i:60;i:2;i:70;i:3;i:80;i:4;i:90;}', 'Mana', 'a:5:{i:0;i:12;i:1;d:10.5;i:2;i:9;i:3;d:7.5;i:4;i:6;}', 328, 'SylasW', 0, 0, 0),
(621, 'Abscond / Abduct', 0, 0, 'Sylas dashes to a location. Sylas can recast the ability to throw his chains out, pulling himself to an enemy he hits.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/SylasE.png', 'a:5:{i:0;i:65;i:1;i:65;i:2;i:65;i:3;i:65;i:4;i:65;}', 'Mana', 'a:5:{i:0;i:13;i:1;i:12;i:2;i:11;i:3;i:10;i:4;i:9;}', 328, 'SylasE', 0, 0, 0),
(622, 'Hijack', 1, 0, 'Sylas steals the enemy\'s ultimate ability and can cast it freely.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/SylasR.png', 'a:3:{i:0;i:75;i:1;i:75;i:2;i:75;}', 'Mana', 'a:3:{i:0;i:80;i:1;i:55;i:2;i:30;}', 328, 'SylasR', 0, 0, 0),
(623, 'Transcendent', 0, 0, 'Syndra\'s spells gain extra effects at max rank.<br><br><font color=\'#FF9900\'>Dark Sphere</font>: Bonus damage against champions.<br><font color=\'#FF9900\'>Force of Will</font>: Bonus true damage.<br><font color=\'#FF9900\'>Scatter the Weak</font>: Increased width.<br><font color=\'#FF9900\'>Unleashed Power</font>: Increased range.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/SyndraPassive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 329, 'passive', 0, 0, 1),
(624, 'Dark Sphere', 1, 0, 'Syndra conjures a Dark Sphere dealing magic damage. The sphere remains and can be manipulated by her other powers.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/SyndraQ.png', 'a:5:{i:0;i:40;i:1;i:50;i:2;i:60;i:3;i:70;i:4;i:80;}', 'Mana', 'a:5:{i:0;i:4;i:1;i:4;i:2;i:4;i:3;i:4;i:4;i:4;}', 329, 'SyndraQ', 0, 0, 0),
(625, 'Force of Will', 1, 0, 'Syndra picks up and throws a Dark Sphere or enemy minion dealing magic damage and slowing the Move Speed of enemies.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/SyndraW.png', 'a:5:{i:0;i:60;i:1;i:70;i:2;i:80;i:3;i:90;i:4;i:100;}', 'Mana', 'a:5:{i:0;i:12;i:1;i:11;i:2;i:10;i:3;i:9;i:4;i:8;}', 329, 'SyndraW', 0, 0, 0),
(626, 'Scatter the Weak', 1, 0, 'Syndra knocks enemies and Dark Spheres back dealing magic damage. Enemies hit by Dark Spheres become stunned.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/SyndraE.png', 'a:5:{i:0;i:50;i:1;i:50;i:2;i:50;i:3;i:50;i:4;i:50;}', 'Mana', 'a:5:{i:0;i:18;i:1;i:17;i:2;i:16;i:3;i:15;i:4;i:14;}', 329, 'SyndraE', 0, 0, 0),
(627, 'Unleashed Power', 1, 0, 'Syndra bombards an enemy Champion with all of her Dark Spheres.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/SyndraR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:120;i:1;i:100;i:2;i:80;}', 329, 'SyndraR', 0, 0, 0),
(628, 'An Acquired Taste', 0, 0, 'Tahm Kench puts the heft of his immense body behind his attacks, gaining extra damage based on his total health. Damaging enemy champions builds stacks of <spellName>An Acquired Taste</spellName>. At three stacks, he can use <spellName>Devour</spellName> on an enemy champion.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/TahmKenchP.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 330, 'passive', 0, 0, 1),
(629, 'Tongue Lash', 1, 0, 'Tahm Kench lashes out with his tongue, damaging and slowing the first unit hit and healing himself if he strikes an enemy champion.<br><br>Applies a stack of  <spellName>An Acquired Taste</spellName> to enemy champions. If the champion already has 3 stacks of <spellName>An Acquired Taste</spellName>, they are stunned and the stacks are consumed.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TahmKenchQ.png', 'a:5:{i:0;i:50;i:1;i:46;i:2;i:42;i:3;i:38;i:4;i:34;}', 'Mana', 'a:5:{i:0;i:7;i:1;d:6.5;i:2;i:6;i:3;d:5.5;i:4;i:5;}', 330, 'TahmKenchQ', 0, 0, 0),
(630, 'Abyssal Dive', 1, 0, 'Dive down and then re-appear at target location, damaging and knocking up all enemies in an area.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TahmKenchW.png', 'a:5:{i:0;i:60;i:1;i:75;i:2;i:90;i:3;i:105;i:4;i:120;}', 'Mana', 'a:5:{i:0;i:21;i:1;i:20;i:2;i:19;i:3;i:18;i:4;i:17;}', 330, 'TahmKenchW', 0, 0, 0),
(631, 'Thick Skin', 1, 0, '<passive>Passive:</passive> Tahm Kench stores a percent of the damage he takes and heals based on it while out of combat.<br><br><active>Active:</active> Convert all stored damage into a temporary shield.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TahmKenchE.png', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 'empty', 'a:5:{i:0;i:3;i:1;i:3;i:2;i:3;i:3;i:3;i:4;i:3;}', 330, 'TahmKenchE', 0, 0, 0),
(632, 'Devour', 0, 0, 'Tahm Kench devours a champion for a few seconds, dealing magic damage if they are an enemy, or shielding them if they are an ally.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TahmKenchRWrapper.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:0;i:1;i:0;i:2;i:0;}', 330, 'TahmKenchRWrapper', 0, 0, 0),
(633, 'Rock Surfing', 0, 0, 'Taliyah gains Move Speed near walls.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Taliyah_Passive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 331, 'passive', 0, 0, 1),
(634, 'Threaded Volley', 1, 0, 'Taliyah throws a volley of rocks in a target direction, moving freely as she casts. This creates Worked Ground below her. If Taliyah casts Threaded Volley on Worked Ground, she consumes the ground to throw a stronger boulder that slows enemies.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TaliyahQ.png', 'a:5:{i:0;i:60;i:1;i:65;i:2;i:70;i:3;i:75;i:4;i:80;}', 'Mana', 'a:5:{i:0;i:7;i:1;i:6;i:2;i:5;i:3;i:4;i:4;i:3;}', 331, 'TaliyahQ', 0, 0, 0),
(635, 'Seismic Shove', 1, 0, 'Taliyah causes an area of ground to erupt and throws enemies within it in a direction of her choosing.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TaliyahWVC.png', 'a:5:{i:0;i:40;i:1;i:30;i:2;i:20;i:3;i:10;i:4;i:0;}', 'Mana', 'a:5:{i:0;i:14;i:1;d:12.5;i:2;i:11;i:3;d:9.5;i:4;i:8;}', 331, 'TaliyahWVC', 0, 0, 0),
(636, 'Unraveled Earth', 1, 0, 'Taliyah creates a slowing minefield. If enemies dash through it or are knocked through it, the mines explode and stun them.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TaliyahE.png', 'a:5:{i:0;i:90;i:1;i:95;i:2;i:100;i:3;i:105;i:4;i:110;}', 'Mana', 'a:5:{i:0;i:16;i:1;i:15;i:2;i:14;i:3;i:13;i:4;i:12;}', 331, 'TaliyahE', 0, 0, 0),
(637, 'Weaver\'s Wall', 1, 0, 'Taliyah creates a very long wall and then surfs it.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TaliyahR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:180;i:1;i:150;i:2;i:120;}', 331, 'TaliyahR', 0, 0, 0),
(638, 'Blade\'s End', 0, 0, 'Talon\'s spells Wound champions and large monsters, stacking up to 3 times. When Talon attacks a champion with 3 stacks of Wound, they bleed for heavy damage over time.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/TalonP.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 332, 'passive', 0, 0, 1),
(639, 'Noxian Diplomacy', 1, 0, 'Talon stabs the target unit. If they are within melee range, this attack deals critical damage. If they are outside melee range, Talon will leap at his target before stabbing them. Talon refunds some health and cooldown if this ability kills the target.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TalonQ.png', 'a:5:{i:0;i:40;i:1;i:40;i:2;i:40;i:3;i:40;i:4;i:40;}', 'Mana', 'a:5:{i:0;i:8;i:1;d:7.5;i:2;i:7;i:3;d:6.5;i:4;i:6;}', 332, 'TalonQ', 0, 0, 0),
(640, 'Rake', 1, 0, 'Talon sends out a volley of daggers that then return back to him, dealing physical damage every time it passes through an enemy. The returning blades deal bonus damage and slow units hit.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TalonW.png', 'a:5:{i:0;i:55;i:1;i:60;i:2;i:65;i:3;i:70;i:4;i:75;}', 'Mana', 'a:5:{i:0;i:9;i:1;d:8.5;i:2;i:8;i:3;d:7.5;i:4;i:7;}', 332, 'TalonW', 0, 0, 0),
(641, 'Assassin\'s Path', 1, 0, 'Talon vaults over any terrain or structure, up to a max distance. This ability has a low cooldown, but puts the used terrain on a long cooldown.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TalonE.png', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 'empty', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 332, 'TalonE', 0, 0, 0),
(642, 'Shadow Assault', 1, 0, 'Talon disperses a ring of blades and becomes Invisible while gaining additional Move Speed. When Talon emerges from Invisibility, the blades converge on his location. Each time the blades move, Shadow Assault deals physical damage to enemies hit by at least one blade.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TalonR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:100;i:1;i:80;i:2;i:60;}', 332, 'TalonR', 0, 0, 0),
(643, 'Bravado', 0, 0, 'Spellcasts empower Taric\'s next 2 basic attacks to deal bonus magic damage, reduce his spell cooldowns, and attack in quick succession.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Taric_Passive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 333, 'passive', 0, 0, 1),
(644, 'Starlight\'s Touch', 1, 0, 'Heals nearby allied champions based on charges stored. Bravado-empowered attacks grant a charge of Starlight\'s Touch.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TaricQ.png', 'a:5:{i:0;i:60;i:1;i:60;i:2;i:60;i:3;i:60;i:4;i:60;}', 'charges', 'a:5:{i:0;i:3;i:1;i:3;i:2;i:3;i:3;i:3;i:4;i:3;}', 333, 'TaricQ', 0, 0, 0),
(645, 'Bastion', 1, 0, 'Passively increase the Armor of Taric and any allied champion with Bastion.<br><br>Actively shields an ally and grants them Bastion for as long as they remain near Taric. Taric\'s spells also cast off the ally with Bastion.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TaricW.png', 'a:5:{i:0;i:60;i:1;i:60;i:2;i:60;i:3;i:60;i:4;i:60;}', 'Mana', 'a:5:{i:0;i:15;i:1;i:15;i:2;i:15;i:3;i:15;i:4;i:15;}', 333, 'TaricW', 0, 0, 0),
(646, 'Dazzle', 1, 0, 'Taric readies a beam of starlight that, after a brief delay, deals magic damage and stuns enemies.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TaricE.png', 'a:5:{i:0;i:40;i:1;i:40;i:2;i:40;i:3;i:40;i:4;i:40;}', 'Mana', 'a:5:{i:0;i:15;i:1;i:14;i:2;i:13;i:3;i:12;i:4;i:11;}', 333, 'TaricE', 0, 0, 0),
(647, 'Cosmic Radiance', 1, 0, 'Pulses cosmic energy onto nearby allied champions after a delay, making them invulnerable for a short duration.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TaricR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:180;i:1;i:150;i:2;i:120;}', 333, 'TaricR', 0, 0, 0),
(648, 'Guerrilla Warfare', 0, 0, 'If Teemo stands still and takes no actions for a short duration, he becomes Invisible indefinitely. If he\'s in brush, Teemo can enter and maintain his Invisibility while moving. After leaving Invisibility, Teemo gains the Element of Surprise, increasing his Attack Speed for a few seconds.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Teemo_P.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 334, 'passive', 0, 0, 1),
(649, 'Blinding Dart', 1, 0, 'Obscures an enemy\'s vision with a powerful venom, dealing damage to the target unit and blinding it for the duration.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/BlindingDart.png', 'a:5:{i:0;i:70;i:1;i:75;i:2;i:80;i:3;i:85;i:4;i:90;}', 'Mana', 'a:5:{i:0;i:7;i:1;i:7;i:2;i:7;i:3;i:7;i:4;i:7;}', 334, 'BlindingDart', 0, 0, 0),
(650, 'Move Quick', 1, 0, 'Teemo scampers around, passively increasing his Move Speed until he is struck by an enemy champion or turret. Teemo can sprint to gain bonus Move Speed that isn\'t stopped by being struck for a short time.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/MoveQuick.png', 'a:5:{i:0;i:40;i:1;i:40;i:2;i:40;i:3;i:40;i:4;i:40;}', 'mana', 'a:5:{i:0;i:14;i:1;i:14;i:2;i:14;i:3;i:14;i:4;i:14;}', 334, 'MoveQuick', 0, 0, 0),
(651, 'Toxic Shot', 0, 0, 'Each of Teemo\'s attacks will poison the target, dealing damage on impact and each second after for 4 seconds.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/ToxicShot.png', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 'empty', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 334, 'ToxicShot', 0, 0, 0),
(652, 'Noxious Trap', 1, 0, 'Teemo throws an explosive poisonous trap using one of the mushrooms stored in his pack. If an enemy steps on the trap, it will release a poisonous cloud, slowing enemies and damaging them over time. If Teemo throws a mushroom onto another mushroom it will bounce, gaining additional range.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TeemoRCast.png', 'a:3:{i:0;i:75;i:1;i:75;i:2;i:75;}', 'Charge', 'a:3:{i:0;d:0.25;i:1;d:0.25;i:2;d:0.25;}', 334, 'TeemoRCast', 0, 0, 0),
(653, 'Damnation', 0, 0, 'Thresh can harvest the souls of enemies that die near him, permanently granting him Armor and Ability Power.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Thresh_Passive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 335, 'passive', 0, 0, 1),
(654, 'Death Sentence', 1, 0, 'Thresh binds an enemy in chains and pulls them toward him. Activating this ability a second time pulls Thresh to the enemy.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/ThreshQ.png', 'a:5:{i:0;i:70;i:1;i:70;i:2;i:70;i:3;i:70;i:4;i:70;}', 'Mana', 'a:5:{i:0;i:20;i:1;i:18;i:2;i:16;i:3;i:14;i:4;i:12;}', 335, 'ThreshQ', 0, 0, 0),
(655, 'Dark Passage', 1, 0, 'Thresh throws out a lantern that shields nearby allied Champions from damage. Allies can click the lantern to dash to Thresh.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/ThreshW.png', 'a:5:{i:0;i:50;i:1;i:55;i:2;i:60;i:3;i:65;i:4;i:70;}', 'Mana', 'a:5:{i:0;i:22;i:1;d:20.5;i:2;i:19;i:3;d:17.5;i:4;i:16;}', 335, 'ThreshW', 0, 0, 0),
(656, 'Flay', 1, 0, 'Thresh\'s attacks wind up, dealing more damage the longer he waits between attacks. When activated, Thresh sweeps his chain, knocking all enemies hit in the direction of the blow.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/ThreshE.png', 'a:5:{i:0;i:60;i:1;i:65;i:2;i:70;i:3;i:75;i:4;i:80;}', 'Mana', 'a:5:{i:0;i:13;i:1;d:12.25;i:2;d:11.5;i:3;d:10.75;i:4;i:10;}', 335, 'ThreshE', 0, 0, 0),
(657, 'The Box', 1, 0, 'A prison of walls that slow and deal damage if broken.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/ThreshRPenta.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'Mana', 'a:3:{i:0;i:140;i:1;i:120;i:2;i:100;}', 335, 'ThreshRPenta', 0, 0, 0),
(658, 'Draw a Bead', 0, 0, 'Increases Tristana\'s Attack Range as she levels.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/passive/Tristana_Passive.png', 'a:1:{i:0;s:4:\"none\";}', 'Champion\'s passive', 'a:1:{i:0;s:4:\"none\";}', 336, 'passive', 0, 0, 1),
(659, 'Rapid Fire', 1, 0, 'Tristana fires her weapon rapidly, increasing her Attack Speed for a short time.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TristanaQ.png', 'a:5:{i:0;i:0;i:1;i:0;i:2;i:0;i:3;i:0;i:4;i:0;}', 'empty', 'a:5:{i:0;i:20;i:1;i:19;i:2;i:18;i:3;i:17;i:4;i:16;}', 336, 'TristanaQ', 0, 0, 0),
(660, 'Rocket Jump', 1, 0, 'Tristana fires at the ground to propel her to a distant location, dealing damage and slowing surrounding units for a brief period where she lands.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TristanaW.png', 'a:5:{i:0;i:60;i:1;i:60;i:2;i:60;i:3;i:60;i:4;i:60;}', 'mana', 'a:5:{i:0;i:22;i:1;i:20;i:2;i:18;i:3;i:16;i:4;i:14;}', 336, 'TristanaW', 0, 0, 0);
INSERT INTO `spell` (`id`, `name`, `is_active`, `is_passive`, `resume`, `spell_image`, `cost`, `type`, `cooldown`, `champion_id`, `spell_id`, `is_toggle`, `is_both`, `is_champ_passive`) VALUES
(661, 'Explosive Charge', 1, 0, 'When Tristana kills a unit, her cannonballs burst into shrapnel, dealing damage to surrounding enemies. Can be activated to place a bomb on a target enemy that explodes after a short duration dealing damage to surrounding units.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TristanaE.png', 'a:5:{i:0;i:50;i:1;i:55;i:2;i:60;i:3;i:65;i:4;i:70;}', 'mana', 'a:5:{i:0;i:16;i:1;d:15.5;i:2;i:15;i:3;d:14.5;i:4;i:14;}', 336, 'TristanaE', 0, 0, 0),
(662, 'Buster Shot', 1, 0, 'Tristana loads a massive cannonball into her weapon and fires it at an enemy unit. This deals Magic Damage and knocks the target back. If the target is carrying the Explosive Charge bomb, the bomb detonation radius is doubled.', 'https://ddragon.leagueoflegends.com/cdn/12.11.1/img/spell/TristanaR.png', 'a:3:{i:0;i:100;i:1;i:100;i:2;i:100;}', 'mana', 'a:3:{i:0;i:120;i:1;i:110;i:2;i:100;}', 336, 'TristanaR', 0, 0, 0),
